import React, { Component } from 'react'
import axios from 'axios'
import DayCard from './DayCard'
import GetCity from '../services/GetCity'

const USERS_REST_API_URL = "http://localhost:8080/api/getWeather"
class UserComponent extends Component {
    state = {
        latitude: '',
        longitude: '',
        time: '',
        city: '',
        isSubmitted: false,
        weatherData: []
    }

    componentDidMount (){
        let self = this;
        navigator.geolocation.getCurrentPosition(function(position) {
            self.setState({ latitude: position.coords.latitude.toString(),
                longitude: position.coords.longitude.toString() });
            self.setState({ time: Math.round((new Date()).getTime() / 1000).toString(),
                        city: GetCity.getCity(self.state.latitude, self.state.longitude)});
            // self.setState({ city: GetCity.getCity(self.state.latitude, self.state.longitude) })
            console.log(self.state);
        })
    }

    onSubmit = (e) => {
        e.preventDefault();
        axios.post(USERS_REST_API_URL, {'latitude':this.state.latitude,
                                        'longitude':this.state.longitude,
                                        'time':this.state.time}).then(response => {
            if (response.data != null) {
                this.setState({ weatherData: response.data})
                this.setState({isSubmitted: true})
            }
        });
    }

    formatDayCards = () => {
        if(this.state.isSubmitted){
            console.log(this.state)
            return this.state.weatherData.map((reading, index) => <DayCard reading={reading} key={index} />)
        }
        return (<div>Press the above button to get Weather data</div>)
    }

    render() {
        return (
            <div>
                <h1 className = "text-center">Weather Data</h1>
                <form onSubmit={this.onSubmit} style={{ borderColor:"black"}}>
                <input 
                    type="submit"
                    value="Click Here to get the Weather Report!"
                    className="btn"
                    style={{flex: '3'}} />
            </form>
            <div className="container">
            <h1 className="display-3 jumbotron">4-Day Forecast</h1>
            <h5 className="display-5 text-muted">{this.state.city}</h5>
                <div className="row justify-content-center">

                {this.formatDayCards()}
        
                </div>
            </div>
            </div>
        )
    }
}

export default UserComponent;
