import React, { Component } from 'react'
import axios from 'axios'

const USERS_REST_API_URL = "http://localhost:8080/api/addUser"
class UserComponent extends Component {

    // constructor(props) {
    //     super(props)
    //     this.state = this.initialState;
    //  }

    state = {
        latitude: '',
        longitude: '',
        time: '',
        users: {}
    }

    componentDidMount (){
        let self = this;
        navigator.geolocation.getCurrentPosition(function(position) {
            self.setState({ latitude: position.coords.latitude.toString(),
                longitude: position.coords.longitude.toString() });
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.setState({ time: Math.round((new Date()).getTime() / 1000).toString()})
        axios.post(USERS_REST_API_URL, {'latitude':this.state.latitude,
                                        'longitude':this.state.longitude,
                                        'time':this.state.time}).then(response => {
            if (response.data != null) {
                this.setState({ location: '' });
                this.setState({ users: response.data })
            }
        });
    }

    onChange = (e) => this.setState({ [e.target.name]: e.target.value });

    render() {
        return (
            <div>
                <h1 className = "text-center">Weather Data</h1>
                <form onSubmit={this.onSubmit} style={{ display: 'flex' }}>
                <input 
                    type="text" 
                    name="location" 
                    style={{ flex: '10', padding: '5px'}}
                    placeholder="Current location by default..."
                    value={this.state.title}
                    onChange={this.onChange}
                    />
                <input 
                    type="submit"
                    value="Submit"
                    className="btn"
                    style={{flex: '1'}} />
            </form>
            <div>
                {
                    this.state.users.map(
                        user =>
                        <h4 key={user}> {user.imperial} </h4>
                    )
                }
            </div>
            </div>
        )
    }
}

export default UserComponent;
