import React, { Component } from 'react'
import axios from 'axios'

class UserComponent extends Component {

    constructor(props) {
        super(props)
        this.state = this.initialState;
     }

    initialState = {
        location: '',
        users: []
    }

    onSubmit = (e) => {
        e.preventDefault();
        axios.post("http://localhost:8080/api/users", this.state.location).then(response => {
            if (response.data != null) {
                this.setState({ users: response.data })
            }
        });
    }

    onChange = (e) => this.setState({ [e.target.name]: e.target.value });

    render() {
        return (
            <div>
                <h1 className = "text-center">Weather Data</h1>
                <form onSubmit={this.onSubmit} style={{ display: 'flex' }}>
                <input 
                    type="text" 
                    name="location" 
                    style={{ flex: '10', padding: '5px'}}
                    placeholder="Current location by default..."
                    value={this.state.title}
                    onChange={this.onChange}
                    />
                <input 
                    type="submit"
                    value="Submit"
                    className="btn"
                    style={{flex: '1'}} />
            </form>
            <div>
                {
                    this.state.users.map(
                        user =>
                        <h4> {user.id}, {user.firstName}, {user.lastName}, {user.email} </h4>
                    )
                }
            </div>
            </div>
        )
    }
}

export default UserComponent;
