import React, { Component } from 'react'
import axios from 'axios'
import DayCard from './DayCard'

const USERS_REST_API_URL = "http://localhost:8080/api/getWeather"
class UserComponent extends Component {

    // constructor(props) {
    //     super(props)
    //     this.state = this.initialState;
    //  }

    state = {
        latitude: '',
        longitude: '',
        time: '',
        isSubmitted: false,
        weatherData: []
    }

    componentDidMount (){
        let self = this;
        navigator.geolocation.getCurrentPosition(function(position) {
            self.setState({ latitude: position.coords.latitude.toString(),
                longitude: position.coords.longitude.toString() });
            self.setState({ time: Math.round((new Date()).getTime() / 1000).toString()});
            self.setState({isSubmitted: true})
        });
    }

    onSubmit = (e) => {
        e.preventDefault();
        axios.post(USERS_REST_API_URL, {'latitude':this.state.latitude,
                                        'longitude':this.state.longitude,
                                        'time':this.state.time}).then(response => {
            if (response.data != null) {
                this.setState({ weatherData: response.data})
                // console.log(response.data['0']['unit'])
                console.log(this.state.weatherData);
                console.log("kadam")
            }
        });
    }

    formatDayCards = () => {
        console.log("pratik")
        // return this.state.weatherData.map((reading, index) => <DayCard reading={reading} key={index} />)
        return (<div>bakwas</div>)
    }

    render() {
        return (
            <div>
                <h1 className = "text-center">Weather Data</h1>
                <form onSubmit={this.onSubmit} style={{ display: 'flex' }}>
                <input 
                    type="submit"
                    value="Submit"
                    className="btn"
                    style={{flex: '3'}} />
            </form>
            <div className="container">
            <h1 className="display-1 jumbotron">4-Day Forecast.</h1>
            <h5 className="display-5 text-muted">New York, US</h5>
                {this.state.isSubmitted ? (
                    <div className="row justify-content-center">

                    {this.formatDayCards()}
          
                  </div>
                ) : (
                    <div></div>
                )}

            </div>
            </div>
        )
    }
}

export default UserComponent;
