import React, { Component } from 'react'
import axios from 'axios'
import DayCard from './DayCard'
import GetCity from '../services/GetCity'
import DegreeToggle from './DegreeToggle'
import Graph from './Graph'

const USERS_REST_API_URL = "http://localhost:8080/api/getWeather"
class UserComponent extends Component {
    constructor(props) {
        super(props)
        this.updateDay = this.updateDay.bind(this)
    }
    state = {
        latitude: '',
        longitude: '',
        time: '',
        city: '',
        isSubmitted: false,
        weatherData: [],
        degreeType: "fahrenheit",
        currentDay: 0
    }

    componentDidMount (){
        let self = this;
        navigator.geolocation.getCurrentPosition(function(position) {
            self.setState({ latitude: position.coords.latitude.toString(),
                longitude: position.coords.longitude.toString() });
            self.setState({ time: Math.round((new Date()).getTime() / 1000).toString(),
                        city: GetCity.getCity(self.state.latitude, self.state.longitude)});
            // self.setState({ city: GetCity.getCity(self.state.latitude, self.state.longitude) })
            console.log(self.state);
        })
    }

    updateForecastDegree = event => {
        this.setState({
          degreeType: event.target.value
        }, () => console.log(this.state))
      }

    onSubmit = (e) => {
        e.preventDefault();
        axios.post(USERS_REST_API_URL, {'latitude':this.state.latitude,
                                        'longitude':this.state.longitude,
                                        'time':this.state.time}).then(response => {
            if (response.data != null) {
                this.setState({ weatherData: response.data})
                this.setState({isSubmitted: true})
            }
        });
    }

    degreeToggleButtons = () => {
        if(this.state.isSubmitted){
            return <DegreeToggle degreeType={this.state.degreeType} updateForecastDegree={this.updateForecastDegree}/>
        }
        return (<div></div>)
    }

    drawGraph = () => {
        if(this.state.isSubmitted){
            // var degreeIndex = 1;
            // if (this.state.degreeType == "celsius"){
            //     degreeIndex = 0;
            // }
            // var hourly = [];
            return this.state.weatherData.map((dayWeather, index) => {
            if (index === this.state.currentDay){
                return (<div className="d-flex justify-content-center">{index} <Graph hourlyData={this.state.weatherData}/></div>)
            }
        })
    }
        return (<div></div>)
    }

    updateDay(dayIndex) {
        this.setState({ currentDay: dayIndex})
    }
    formatDayCards = () => {
        if(this.state.isSubmitted){
            console.log(this.state)
            return this.state.weatherData.map((reading, index) =>
                <DayCard reading={reading} key={index} degreeType={this.state.degreeType} updateDay={this.updateDay}/>)
        }
        return (<div>Press the above button to get Weather data</div>)
    }

    render() {
        return (
            <div>
                <h1 className = "text-center">Weather Data</h1>
                <form onSubmit={this.onSubmit} style={{ border: '3'}}>
                <input 
                    type="submit"
                    value="Click Here to get the Weather Report!"
                    className="btn"
                    style={{flex: '3'}} />
            </form>
            <div className="container">
            <h1 className="display-3 jumbotron">4-Day Forecast</h1>
            <h5 className="display-5 text-muted">{this.state.city}</h5>
            <div>{this.degreeToggleButtons()}</div><br />
                <div className="row justify-content-center">
                {this.formatDayCards()}
                </div><br />
                {this.drawGraph()}
            </div>
            </div>
        )
    }
}

export default UserComponent;
